template <
    typename Target,
    std::size_t Index = 0,
    typename Current  = std::tuple<>,
    enable_if<Index == std::tuple_size<Target>::value> = 0
>
inline Target construct(
    const xalan::XPathExecutionContext::XObjectArgVectorType&,
    Current&& current
) {
    return current;
}
